import { Component } from 'react';
import Select from 'react-select';
/* import 'bootstrap/dist/css/bootstrap.min.css';
import '../styles.css' */
import axios from 'axios'

const techCompanies = [
  { label: "Apple", value: 1 },
  { label: "Facebook", value: 2 },
  { label: "Netflix", value: 3 },
  { label: "Tesla", value: 4 },
  { label: "Amazon", value: 5 },
  { label: "Alphabet", value: 6 },
];

class App extends Component {

  static async getInitialProps() {
    let response = await axios.get('http://localhost:3000/api/posts');
    return { posts: response.data };
  }
  
  render() {
    return (
      <div className='container'>
        <style jsx>{`
            .header {
              padding: 16px 16px;
            }
            .content {
              padding: 16px 16px;
            }
            .post {
              margin-bottom: 16px;
            }
        `}</style>
         <div>
              <ul id="nav">
                <li><a href="http://localhost:3000/keystone">Ir a la UI Administrador</a></li>
                <li><a href="#">About</a></li>
                <li><a href="#">FAQ</a></li>
                <li><a href="#">Contact</a></li>
              </ul>
            </div>
        <div className='header'>
          <h1>Aqui va un titulo y un navbar</h1>
       </div>
      { this.props.posts.map((post, i) => {
          <div className="container">
      <div className="row">
        <div className="col-md-4"></div>
        <div className="col-md-4">
       // return (
          <Select options={ post.title } />
        </div>
      <div className="col-md-4"></div>
    </div>
  </div>

       // )
      }) }
        
  
        <div className='content'>
          { this.props.posts.map((post, i) => {
              return (
                <div className='post' key={i}>
                  <div className='row'>
                    <div className='col-12 col-md-4'>
                      <img className='img-fluid' src={post.image.secure_url}/>
                    </div>
                    <div className='col-12 col-md-8'>
                      <h2>{post.title}</h2>
                      <div dangerouslySetInnerHTML={{__html: post.content.brief}}></div>
                    </div>
                  </div>
                </div>
              );
            }) }
        </div>
      </div>
    );
  };
}

export default App;
